def on_forever():
    global nbrA
    if input.button_is_pressed(Button.A):
        if 255 == nbrA:
            nbrA = 0
            basic.pause(1000)
            led.toggle(2, 0)
        elif 0 == nbrA:
            nbrA += 255
            basic.pause(1000)
            led.toggle(2, 0)
        else:
            pass
    robotbit.motor_run(robotbit.Motors.M2B, nbrA)
    pins.analog_write_pin(AnalogPin.P19, 1023)
basic.forever(on_forever)
